export const onSelectHandler = (ctx, event, key) => {
  const TARGET = event.target;
  ctx.setState({
    [key]: TARGET.value,
  });
};

export const onInputChangeHandler = (event) => {
  const TARGET = event.target;
  this.setState({
    [TARGET.name]: TARGET.value,
  });
};
