'use-strict';
import { HEALTH_CATEGORIES, USER_ACCOUNT_TYPE } from '../constants';

const log = console.log;

export const addUserHandler = (ctx, newUser) => {
  log('Adding user...');
  const users = ctx.state.userDB;

  users[newUser.hash] = newUser;
  ctx.setState({
    userDB: users,
  });
  log(ctx.state.userDB);
};

export const signUpUser = (signUpCtx) => {
  log('Creating user...');
  const inputs = signUpCtx.state;
  const newUser = new User(inputs.first, inputs.last, inputs.email, inputs.password, inputs.sex);
  signUpCtx.props.addUserHandler(newUser);
  log('User successfully added');
  _clearSignUpInputs(signUpCtx);
};

const _signUpInputValidate = (locCtx) => {
  log('Validating sign-up inputs...');
  const isNameValid = _isInvalid(locCtx.state.locationName);
  const isAddressValid = _isInvalid(locCtx.state.address);
  const isImageUrlValid = _isInvalid(locCtx.state.imageUrl);
  const isMaxOccValid = _isInvalid(locCtx.state.maxOccupancy);
  const isDescValid = _isInvalid(locCtx.state.description);

  locCtx.setState({
    isNameValid: isNameValid,
    isAddressValid: isAddressValid,
    isImageUrlValid: isImageUrlValid,
    isMaxOccValid: isMaxOccValid,
    isDescValid: isDescValid,
  });

  return isNameValid && isAddressValid && isImageUrlValid && isMaxOccValid && isDescValid;
};

const _isInvalid = (value) => {
  return (
    value !== null &&
    value !== undefined &&
    value !== '' &&
    value !== 'select' &&
    value !== 'undefined'
  );
};

export class User {
  constructor(firstName, lastName, email, password, sex) {
    this.firstName = firstName;
    this.lastName = lastName;
    this.email = email;
    this.password = password;
    this.type = USER_ACCOUNT_TYPE;
    this.sex = sex;
    this.hash = this.email + this.password;
    this.reminders = {
      [HEALTH_CATEGORIES.medical]: [],
      [HEALTH_CATEGORIES.mental]: [],
      [HEALTH_CATEGORIES.phsycial]: [],
    };
    this.notifications = [];
  }

  getHash = () => {
    return this.hash;
  };
}

const _clearSignUpInputs = (ctx) => {
  ctx.setState({
    first: '',
    last: '',
    email: '',
    password: '',
    sex: '',
  });
};
