import { LOCATIONS } from '../constants'; 

const log = console.log;

export const viewPlace = (ctx, location) => {
    log(`Viewing ${location}...`);
    log(`Displaying place details ${JSON.stringify(LOCATIONS[location])}...`);
    ctx.setState({
        locationViewed: LOCATIONS[location]
    });
}

export const checkInHandler = (ctx, location) => {
    const { activeUser } =  ctx.state;
    activeUser.checkedInLocation = location
    log(`Checking in user...`);
    ctx.setState({
        activeUser: activeUser
    });
    log(`User successfully checked in at to ${ctx.state.activeUser.checkedInLocation?.name}`);
}

export class Location {
  constructor(name, maxOccupancy, currOccupancy, address, description, image) {
    this.name = name;
    this.address = address;
    this.country = 'Canada';
    this.maxOccupancy = maxOccupancy;
    this.currOccupancy = currOccupancy;
    this.description = description;
    this.imageUrl = image;
  }
}
