import React, { Component } from 'react';
import { onInputChangeHandler } from '../../../actions/utils' 
import './styles.css';

class LoginForm extends Component {
  state = {
    email: '',
    password: '',
    invalidLogin: this.props.invalidLoginStatus,
  };

  onInputChangeHandler = (event) => {
    const TARGET = event.target;
    this.setState({
      [TARGET.name]: TARGET.value,
    });
  };

  displayErrorMsg = () => {
    const { isNameValid, isAddressValid, isImageUrlValid, isMaxOccValid, isDescValid } = this.state;
    if (isNameValid && isAddressValid && isImageUrlValid && isMaxOccValid && isDescValid) {
      return '';
    } else {
      return <span className="errorMsg"> Input fields in red are missing</span>;
    }
  };

  render() {
    return (
      <div id="LoginFrom">
        {this.displayErrorMsg()}
        <input
          className={`space input ${this.props.invalidLoginStatus ? 'invalidInput' : ''}`}
          type="email"
          value={this.state.email}
          onChange={onInputChangeHandler}
          placeholder="email"
          name="email"
        />
        <input
          className={`space input ${this.props.invalidLoginStatus ? 'invalidInput' : ''}`}
          type="password"
          value={this.state.password}
          onChange={this.onInputChangeHandler}
          name="password"
          placeholder="password"
        />
        <button
          className="space primary-btn"
          onClick={() => {
            this.props.loginHandler(this.state.email, this.state.password);
          }}
        >
          {' '}
          login{' '}
        </button>
      </div>
    );
  }
}

export default LoginForm;
