import React, { Component } from 'react';
import ListSelector from '../../General/ListSelector'
import { onSelectHandler, onInputChangeHandler} from '../../../actions/utils'
import './styles.css'
import { HEALTH_CATEGORIES, HEALTH_SUB_CATEGORIES } from '../../../constants';

class AddReminder extends Component {
  state = {
    category: this.props?.category,
    subCategory: this.props?.subCategory,
    reminderName: this.props?.reminderName,
    remiderTime: this.props?.remiderTime,
    reminderNote: this.props?.reminderNote,
    isCategoryValid: true,
    isSubCategoryValid: true,
    isNameValid: true,
    isDateTimeValid: true,
    isNoteValid: true,
  }

  render() {
    return (
      <div id="AddReminderWrapper" >
        <div id="AddReminderWindow" className="windowWrapper">
          <h1> Add Reminder </h1>
          <br/>
          <div className="reminderInputWrapper">
            <span className="inputLabel"> Category: </span>
            <ListSelector 
              id="HealthCatList"
              className={this.state.isCategoryValid ? '' : 'invalidInput'}
              name="categories"
              autoComplete="false"
              defaultValue={this.state.category === undefined ? '' : this.state.category}
              options={Object.values(HEALTH_CATEGORIES)}
              onChangeHandler={(event)=>{onSelectHandler(this, event, "category")}}
            />
          </div>
          {this.state.category !== undefined ?
            <div className="reminderInputWrapper">
              <span className="inputLabel"> Sub-Category: </span>
              <ListSelector 
                id="SubHealthCatList"
                className={this.state.isSubCategoryValid ? '' : 'invalidInput'}
                name="subCategories"
                autoComplete="false"
                defaultValue={this.state.subCategory === undefined ? '' : this.state.subCategory}
                options={HEALTH_SUB_CATEGORIES[this.state.category]}
                onChangeHandler={(event)=>{onSelectHandler(this, event, "subCategory")}}
              /> 
            </div> : ''
          }
          <div className="reminderInputWrapper">
            <span className="inputLabel"> Title: </span>
            <input 
              id="ReminderTitleInput"
              className={`input ${this.state.isNameValid ? '' : 'invalidInput'}`}
              type="text" 
              name="reminderName" 
              placeholder="Title"
              onChange={(event) => {onInputChangeHandler(this, event)}}
            />
          </div>
          <div className="reminderInputWrapper">
            <span className="inputLabel"> When to remind: </span>
            <input 
              className={`input ${this.state.isDateTimeValid ? '' : 'invalidInput'}`}
              type="datetime-local" 
              id="DateTimeInput"
              name="reminderTime" 
              value={this.state.remiderTime}
              min={(new Date())}
              onChange={(event) => {onInputChangeHandler(this, event)}}
            />
          </div>
          <div className="reminderInputWrapper">
            <span className="inputLabel"> Notes: </span>
            <textarea 
              id="ReminderNotesInput"
              className={`input ${this.state.isNoteValid ? '' : 'invalidInput'}`}  
              name="reminderNote"
              maxLength='225' 
              placeholder="Notes"
              onChange={(event) => {onInputChangeHandler(this, event)}}
            />
            <br/>
            <span className="charCount">{this.state.reminderNote !== undefined ? this.state['reminderNote'].length : '0'}/225</span>
          </div>
          <button className="primary-btn" onClick={()=> {this.props.addReminderHandler(this)}}> Add </button>
        </div>
      </div>
    );
  };
}

export default AddReminder;
