'use-strict'

const log = console.log;

export const saveUserInfoHandler = (ctx, setCtx) => {
  log("Upating user info...")
  const user = ctx.state.activeUser;
  user.firstName = setCtx.state.first;
  user.lastName = setCtx.state.last;
  user.email = setCtx.state.email;
  user.password = setCtx.state.pass;
  user.sex = setCtx.state.sex;
  ctx.setState({
    activeUser: user
  }, () => {log("Upating user info...", ctx.state.activeUser)});
}

const _UserInputValidate = (setCtx) => {
  log('Validating reminder inputs...');
  const isNameValid = _isInvalid(setCtx.state.first);
  const isAddressValid = _isInvalid(setCtx.state.last);
  const isImageUrlValid = _isInvalid(setCtx.state.email);
  const isMaxOccValid = _isInvalid(setCtx.state.pass);
  const isDescValid = _isInvalid(setCtx.state.sex);

  locCtx.setState({
    isNameValid: isNameValid,
    isAddressValid: isAddressValid,
    isImageUrlValid: isImageUrlValid,
    isMaxOccValid: isMaxOccValid,
    isDescValid: isDescValid,
  });

  return isNameValid && isAddressValid && isImageUrlValid && isMaxOccValid && isDescValid;
};

const _isInvalid = (value) => {
  return (
    value !== null &&
    value !== undefined &&
    value !== '' &&
    value !== 'select'
  );
};
